import React, { useState } from "react";

function App() {
  const [contact, setContact] = useState({
    fName: "",
    lName: "",
    email: ""
  });

  function handleChange(event){

    const {value, name} = event.target;
    console.log(value);

    setContact((prevValue)=>{
      if(name === "fName"){
        return {
          fName: value,
          lName: prevValue.lName,
          email: prevValue.email
        }
      }
      else if(name === "lName"){
        return {
          fName: prevValue.fName,
          lName: value,
          email: prevValue.email
        }
      }
      else if (name === "email"){
        return {
          fName: prevValue.fName,
          lName: prevValue.lName,
          email: value
        }
      }
    });

  }

  return (
    <div className="container">
      <h1>
        Hello {contact.fName} {contact.lName}
      </h1>
      <p>{contact.email}</p>
      <form>
        <input onClick={handleChange} name="fName" placeholder="First Name" />
        <input onClick={handleChange} name="lName" placeholder="Last Name" value={contact.lName}/>
        <input onClick={handleChange} name="email" placeholder="Email" value={contact.email}/>
        <button>Submit</button>
      </form>
    </div>
  );
}

export default App;
