import React from "react";
import Login from "./Login";
import Input from "./Input";
var isLoggedIn = true;

function renderConditionally(){

  if(isLoggedIn){
    return <h1>Hello</h1>
  }else{
    return(
      <Login>
        <Input />
        <button type="submit">Login</button>
      </Login>


    )
  }

}

function App() {
  return (
    <div className="container">{renderConditionally()}</div>
  );
}

export default App;
